/** @jsx jsx */

import { css, jsx } from '@emotion/react';
import { N800 } from '@atlaskit/theme/colors';
const linkStyles = css({
  color: `var(--ds-text, ${N800})`,
  cursor: 'pointer'
});

/**
 * __Progress tracker link__
 */
const Link = ({
  href,
  onClick,
  label,
  testId
}) => jsx("a", {
  css: linkStyles,
  href: href,
  onClick: onClick,
  "data-testid": testId
}, label);
export default Link;